styles:
ay-button {
  margin: 8px 12px;
  font-size: 1em;
  line-height: 1em;
  vertical-align: top;
  display: inline-block; }
  ay-button .fa {
    margin-right: .5em; }
  ay-button button {
    line-height: 1.5em;
    width: 100%;
    display: inline-block;
    border-radius: 2em;
    padding: .25em 1.5em;
    border: 1px solid transparent;
    cursor: pointer;
    transition: all .2s linear; }
    ay-button button:focus, ay-button button:hover {
      outline: none;
      box-shadow: 0 1px 7px rgba(0, 0, 0, 0.05), 0 7px 17px rgba(0, 0, 0, 0.1); }
    ay-button button.active {
      box-shadow: 0 1px 5px rgba(0, 0, 0, 0.1), 0 5px 13px rgba(0, 0, 0, 0.2); }
    ay-button button.disable {
      cursor: not-allowed; }
      ay-button button.disable:focus, ay-button button.disable:hover {
        outline: none;
        box-shadow: none; }

ay-button.square button {
  border-radius: 3px; }

.button-group {
  margin: 8px 12px; }
  .button-group ay-button {
    margin: 0; }
    .button-group ay-button button {
      border-radius: 0;
      padding-left: 1em;
      padding-right: 1em;
      border-right-width: 0 !important; }
  .button-group ay-button:first-child button {
    border-radius: 2em 0 0 2em;
    padding-left: 1.5em; }
  .button-group ay-button:last-child button {
    border-radius: 0 2em 2em 0;
    padding-right: 1.5em;
    border-right-width: 1px !important; }
  .button-group.square ay-button:first-child button {
    border-radius: 3px 0 0 3px;
    padding-left: 1em; }
  .button-group.square ay-button:last-child button {
    border-radius: 0 3px 3px 0;
    padding-right: 1em; }

