styles:
ay-dividing {
  display: block;
  margin: 1.5em 0;
  color: #999;
  height: 1em;
  display: flex;
  flex-direction: row;
  text-align: center; }
  ay-dividing .border {
    flex: 1;
    border-top: 1px solid #CCC;
    margin: .5em;
    margin-bottom: 0;
    height: .5em; }
  ay-dividing .content {
    line-height: 1em;
    vertical-align: top; }
  ay-dividing .left {
    margin-right: 1em; }
  ay-dividing .right {
    margin-left: 1em; }
  ay-dividing .left.empty {
    margin-right: 0em; }
  ay-dividing .right.empty {
    margin-left: 0em; }

.flex-row > ay-dividing {
  flex-direction: column;
  margin: 0 1.5em;
  height: inherit; }
  .flex-row > ay-dividing .border {
    border: none;
    border-right: 1px solid #CCC;
    margin: 0; }
  .flex-row > ay-dividing .content {
    width: 1em; }
  .flex-row > ay-dividing .left {
    margin-bottom: 1em;
    margin-right: .5em; }
  .flex-row > ay-dividing .right {
    margin-top: 1em;
    margin-right: .5em; }
  .flex-row > ay-dividing .left.empty {
    margin-bottom: 0em;
    margin-right: .5em; }
  .flex-row > ay-dividing .right.empty {
    margin-top: 0em;
    margin-right: .5em; }

