styles:
#toasty {
  position: fixed;
  z-index: 999999;
  bottom: 1em;
  right: 1em;
  text-align: right; }
  #toasty .toast {
    text-align: left;
    display: inline-block;
    transition: all .5s ease-in;
    cursor: pointer;
    margin-top: .5em;
    padding: .5em 2em;
    right: 0;
    box-shadow: 0px 1px 7px rgba(0, 0, 0, 0.1), 0px 7px 17px rgba(0, 0, 0, 0.1);
    opacity: 1;
    position: relative;
    filter: blur(0px); }
    #toasty .toast .icon {
      width: 2em;
      transform: translateX(-1em);
      display: flex;
      text-align: center;
      align-items: center; }
      #toasty .toast .icon i {
        margin: 0 auto; }
    #toasty .toast .close {
      position: absolute;
      top: .5em;
      right: 10px;
      font-size: 1em;
      line-height: 1.5em; }
      #toasty .toast .close:focus {
        outline: 0; }
    #toasty .toast .title {
      line-height: 1.5em;
      font-size: 1em; }
    #toasty .toast .message {
      line-height: 1.5em;
      font-size: .8em; }
    #toasty .toast.hidden {
      opacity: 0;
      filter: blur(10px); }

