styles:
ay-toggle {
  display: inline-block;
  position: relative;
  height: 1.5em;
  width: 3em;
  vertical-align: top;
  padding: .25em 0; }
  ay-toggle .track {
    display: inline-block;
    position: relative;
    border: none;
    border-radius: 1.5em;
    cursor: pointer;
    width: 3em;
    height: 1.5em;
    outline: none;
    box-sizing: border-box;
    padding: 0;
    transition: all .1s linear;
    background: #333; }
    ay-toggle .track.on {
      background: #308BC8; }
      ay-toggle .track.on .thumb {
        left: 1.5625em; }
        ay-toggle .track.on .thumb .fa {
          color: #22618C; }
    ay-toggle .track:focus {
      box-shadow: 0 1px 7px rgba(0, 0, 0, 0.05), 0 7px 17px rgba(0, 0, 0, 0.1); }
      ay-toggle .track:focus .thumb {
        box-shadow: 0 1px 7px rgba(0, 0, 0, 0.05), 0 7px 17px rgba(0, 0, 0, 0.1); }
    ay-toggle .track.isDisable {
      cursor: not-allowed;
      box-shadow: none; }
      ay-toggle .track.isDisable .thumb {
        cursor: not-allowed;
        box-shadow: none; }
  ay-toggle .thumb {
    display: inline-flex;
    position: absolute;
    border: none;
    cursor: pointer;
    outline: none;
    left: 0;
    top: 0;
    box-sizing: border-box;
    border: 1px solid transparent;
    vertical-align: middle;
    width: 1.5em;
    height: 1.5em;
    border-radius: 1em;
    background: #F2F2F2;
    z-index: 200;
    transition: all .1s linear; }
    ay-toggle .thumb .fa {
      font-size: .8em;
      color: #000;
      color: #ff2174;
      align-items: center;
      justify-content: center;
      width: 100%;
      height: 1em;
      align-self: center; }

