/**
 * 顯示函示執行過程、結果、時間
 * @export
 * @param {any} target
 * @param {any} key
 * @param {any} descriptor
 */
export function log(target, key, descriptor) {
  const func = descriptor.value;
  descriptor.value = (...params) => {
    const start = Date.now();
    const result = func.apply(this, [...params]);
    const end = Date.now();
    console.log(`${target.name}.${key}(${params}) = ${result} (${end - start}ms)`);
    return result;
  };
  return descriptor;
}
