import * as mocha from "mocha";
import * as should from "should";
require("should");
import { Error } from "./error";
describe("Error", () => {
  let error = new Error("A0001", "發生錯誤", "詳細資料");
  error.code.should.equal("A0001");
  error.message.should.equal("發生錯誤");
  error.detail.should.equal("詳細資料");
});
describe("Error", () => {
  let error = new Error("A0001", "發生錯誤");
  error.code.should.equal("A0001");
  error.message.should.equal("發生錯誤");
  error.detail.should.equal("");
});
