import { logger } from "./logger";
import * as mocha from "mocha";
import * as should from "should";
import * as assert from "assert";
require("should");

logger.subscribe(e => {

});

describe("logger", () => {
  it("info", (done) => {
    let scription = logger.subscribe(e => {
      e.type.should.equal("info");
      e.title.should.equal("content");
      scription.unsubscribe();
      done();
    });
    logger.info("content");
  });

  it("log", (done) => {
    let scription = logger.subscribe(e => {
      e.type.should.equal("log");
      scription.unsubscribe();
      done();
    });
    logger.log("content");
  });

  it("debug", (done) => {
    let scription = logger.subscribe(e => {
      e.type.should.equal("debug");
      scription.unsubscribe();
      done();
    });
    logger.debug("debug");
  });

  it("warning", (done) => {
    let scription = logger.subscribe(e => {
      e.type.should.equal("warning");
      scription.unsubscribe();
      done();
    });
    logger.warning("content");
  });

  it("error", (done) => {
    let scription = logger.subscribe(e => {
      e.type.should.equal("error");
      scription.unsubscribe();
      done();
    });
    logger.error("content");
  });

});
