import * as mocha from "mocha";
import * as should from "should";
import * as assert from "assert";
require("should");
import { range } from "./range";

describe("range", () => {
  it("max", () => {
    range(0, 100, 10).should.equal(10);
  });
  it("min", () => {
    range(0, -100, 10).should.equal(0);
  });
  it("normal", () => {
    range(0, 50, 100).should.equal(50);
  });
});